catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	g_form.setDisplay('geo', false);
	g_form.setDisplay('country', false);
	g_form.setDisplay('company', false);
	g_form.setDisplay('step2_variable1', false);
	g_form.setDisplay('step2_variable2', false);
	g_form.setDisplay('step3_variable1', false);
	g_form.setDisplay('step3_variable2', false);
	g_form.setDisplay('step4_variable1', false);
	g_form.setDisplay('step4_variable2', false);
	g_form.setDisplay('requested_by', false);
	g_form.setDisplay('alternate_buyer', false);
	if (newValue == 1) {
		g_form.setDisplay('geo', true);
		g_form.setDisplay('country', true);
		g_form.setDisplay('company', true);
	}
	if (newValue == 2) {
		g_form.setDisplay('step2_variable1', true);
		g_form.setDisplay('step2_variable2', true);
	}
	else if (newValue == 3) {
		g_form.setDisplay('step3_variable1', true);
		g_form.setDisplay('step3_variable2', true);
	}
	else if (newValue == 4) {
		g_form.setDisplay('step4_variable1', true);
		g_form.setDisplay('step4_variable2', true);

	}
	else if (newValue == 5) {
		g_form.setDisplay('requested_by', true);
		g_form.setDisplay('alternate_buyer', true);
	}
}
