sp_widget.client_script:
api.controller = function($scope) {
    /* widget controller */
    var c = this;
    // initialize section
    c.currentSection = 1; 

    c.setStep = function(val, e) {
        // check the gform
        var gform = $scope.page && $scope.page.g_form;
        if (gform) {
            // 确保val为有效数值且大于0，否则使用默认值1
            val = parseInt(val) > 0? parseInt(val) : 1; 
            gform.setValue('current_section', val);
            c.currentSection = val;
        }

        // 使用querySelectorAll获取元素，返回的是真正的数组，便于操作
        var list = document.querySelectorAll('.btn - primary'); 
        list.forEach(function(element) {
            element.classList.remove('btn - primary');
        });

        if (val > 0) {
            if (e && e.currentTarget) {
                e.currentTarget.classList.remove('btn - default');
                e.currentTarget.classList.add('btn - primary');
            }
        } else {
            if (e && e.currentTarget) {
                var nextSibling = e.currentTarget.nextElementSibling;
                if (nextSibling) {
                    nextSibling.classList.add('btn - primary');
                }
                e.currentTarget.classList.add('btn - default');
            }
        }
    };
};

