sp_widget.client_script:
api.controller = function($scope) {
    /* widget controller */
    var c = this;
    // initialize section
    c.currentSection = 1; 

    c.setStep = function(val, e) {
        // check the gform
        var gform = $scope.page && $scope.page.g_form;
        if (gform) {
            val = parseInt(val) > 0 ? parseInt(val) : 1; 
            gform.setValue('current_section', val);
            c.currentSection = val;
        }

        // Remove the active state from all buttons
        var buttons = document.querySelectorAll('.btn-breadcrumb .btn'); 
        buttons.forEach(function(button) {
            button.classList.remove('active');
        });

        // Add the active state to the currently clicked button
        if (e && e.currentTarget) {
            e.currentTarget.classList.add('active');
        }
    };
};

sp_widget.css:
.btn-breadcrumb .btn {
